DROP TABLE IF EXISTS TELEFONES;

# CRIAÇÃO DA TABELA ADICIONAL
CREATE TABLE IF NOT EXISTS TELEFONES (
	id_telefone INT UNSIGNED NOT NULL AUTO_INCREMENT,
    ddd VARCHAR(3) NOT NULL,
    numero VARCHAR(15) NOT NULL,
    operadora VARCHAR(10),
    tipo VARCHAR(20) NOT NULL,
    PRIMARY KEY (id_telefone)
) ENGINE=InnoDB;

# ALTERAÇÃO DA TABELA ADICIONAL PARA FOREIGN KEY
ALTER TABLE TELEFONES
	ADD id_funcionario INT UNSIGNED NOT NULL,
    ADD CONSTRAINT fk_FUNC
		FOREIGN KEY (id_funcionario)
        REFERENCES FUNCIONARIOS(id_funcionario);

